package BasicJavaProgram;

public class DefaultValuesInJava {
	static int  num1;
	static double d;
	static char ch;
	static String s;
	static boolean b;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("The default value of integer datatype is:"+num1);
		System.out.println("The default value of double datatype is"+d);
		System.out.println("The default value of character datatype is:"+ch);
		System.out.println("The default value of string datatype is:"+s);
		System.out.println("The default value of boolean datatype is:"+b);
		

	}

}
