package javalearning;

public class DefaultValues {
	static int num1;
	static double num2;
	static char c;
	static boolean b;
	static String s;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Printing the default values of data types");
		System.out.println("The default value of int is:"+num1);
		System.out.println("The default value of double is:"+num2);
		System.out.println("The default value of char is:"+c);
		System.out.println("The default value of boolean is:"+b);
		System.out.println("The default value of string is:"+s);
	}

}
