package ClassAndObjectInJava;

public class Car {
	String brand="BMW";
	String model="2024 Model"; 
	String price="Rs.5crore";
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Car c = new Car();
		System.out.println("The car is "+c.brand);
		System.out.println("The model is "+c.model);
		System.out.println("The price is "+c.price);
		

	}

}
